mapper.update:
UPDATE notice


    title=#{title},


        content=#{content}


WHERE notice_id=#{noticeId} AND tea_id=#{teaId}

mapper.delete:
DELETE
FROM notice
WHERE notice_id=#{noticeId}
            AND
      tea_id=#{teaId}
            AND
      class_id=#{classId}

mapper.select:
SELECT class_id
FROM class
WHERE tea_id=#{tea_id};
