mapper.insert:
INSERT INTO score_sign
SET stu_id = #{studentPk}
    , year = #{year}
    , semester = #{semester}
    , pic = #{pic}
    , exam_sign = #{examSign}

mapper.select:
SELECT stu_id AS stuId
        , year
        , semester
FROM score_sign
WHERE stu_id = #{stuId}
AND year = #{year}
AND semester = #{semester}

mapper.delete:
DELETE FROM score_sign
WHERE stu_id = #{stuId}
AND year = #{year}
AND semester = #{semester}

mapper.select:
SELECT parents_id AS parentId , uid , upw , nm , phone , sub_phone AS subPhone , email , connet , auth , addr , acept , created_at AS createdAt , updated_at AS updatedAt FROM parents
parents_id = #{signedUserId}
ORDER BY parents_id

mapper.update:
