    , name = #{nm}
    , phone = #{phone}
    , sub_phone = #{subPhone}
    , email = #{email}
    , connect = #{connect}
    , addr = #{addrs}

mapper.select:
SELECT *
FROM parents


        uid = #{uid}


        and email = #{email}

SELECT uid
FROM parents
WHERE name = #{nm}
AND phone = #{phone}

mapper.update:
