mapper.insert:
INSERT INTO score_sign
SET stu_id = #{stuId}
, year = #{year}
, semester = #{semester}
, pic = #{pic}
, exam_sign = #{examSign}

mapper.update:
UPDATE score_sign
SET pic = #{pic}
WHERE sign_id = #{signId}

mapper.select:
SELECT stu_id AS stuId
        , year
        , semester
FROM score_sign
WHERE stu_id = #{stuId}
AND year = #{year}
AND semester = #{semester}
AND exam_sign = #{examSign}

mapper.delete:
DELETE FROM score_sign
WHERE stu_id = #{studentPk}
AND year = #{year}
AND semester = #{semester}
AND exam_sign = #{examSign}

mapper.select:
