mapper.update:
UPDATE student
SET state = #{state}
WHERE stu_id = #{pk}

UPDATE student


        name = #{name}


        , phone = #{phone}


        , addr = #{fullAddr}


        , etc = #{etc}


WHERE stu_id = #{pk}

UPDATE student
SET grade = #{grade}, etc = null
WHERE stu_id = #{studentPk}

mapper.select:
SELECT etc
FROM student
WHERE stu_id = #{pk}

mapper.update:
UPDATE student_classes
SET etc = #{etc}
WHERE sc_id = (
SELECT MAX(sc_id)
FROM student_classes
WHERE stu_id = #{pk}
)
